import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import {LedgerDetail} from "./model/LedgerDetail";
import LedgerDetailTable from "./components/LedgerDetailTable";

function App() {
  const [invoices, setInvoices] = useState<LedgerDetail[]>([]);
  function getData() {
    return axios.get("http://localhost:8080/invoices").then(response => {
      const data = response.data.map((value: any) => {
        return new LedgerDetail(
            value.amount,
            value.currency,
            value.is_credit,
            value.description,
            value.created_at
        );
      });
      setInvoices(data);
    }).catch(error => {
      console.error(error)
      setInvoices([]);
    });
  }
  getData();

  return (
    <div className="App">
      <header className="App-header">
        <p className="simple-header">My Ledger</p>
        <p className="simple-entries">Total Entries: {invoices.length}</p>
        <LedgerDetailTable ledgerDetails={invoices}></LedgerDetailTable>
      </header>
    </div>
  );
}

export default App;
