import TableContainer from "@mui/material/TableContainer";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import TableCell from "@mui/material/TableCell";
import TableBody from "@mui/material/TableBody";
import * as React from "react";
import {Component} from "react";
import {Ledger} from "../model/Ledger";
import axios from "axios";
import axiosRetry from "axios-retry";
import Backdrop from "@mui/material/Backdrop";
import CircularProgress from "@mui/material/CircularProgress";
import Button from "@mui/material/Button";
import LedgerDetailTable from "../components/LedgerDetailTable";

export class LedgersList extends Component {

    state: {ledgers: Ledger[], isLoading: boolean, idsSeen: number[], hasMore: boolean} = {
        ledgers: [],
        isLoading: false,
        idsSeen: [],
        hasMore: false
    }

    setLedgers(ledgers: Ledger[], hasMore: boolean) {

        if (hasMore) {
            this.state.idsSeen.push(ledgers[ledgers.length - 1].id as number)
        }
        this.setState({
            isLoading: false,
            ledgers: ledgers,
            hasMore: hasMore
        });
    }

    componentDidMount() {
        this.getData(false)
    }

    async getData(isPrevious: Boolean) {
        console.log("lastIdSeen: ");
        if (this.state.idsSeen.length > 0) {
            if (isPrevious) {
                const newArray = this.state.idsSeen.slice(0, -1);
                this.setState({ idsSeen: newArray });
                console.log("has value?")
            }
        }
        axiosRetry(axios, { retries: 3});
        let lastIdSeen
        if (this.state.idsSeen.length == 0) {
            lastIdSeen = null
        }
        else {
            lastIdSeen = this.state.idsSeen[this.state.idsSeen.length - 1]
        }
        console.log("lastIdSeen: " + lastIdSeen);
        axios.get("http://localhost:8080/ledger" , { params: { lastId : lastIdSeen, limit: 10}}).then((response) => {
            const json = response.data;
            const ledgers = json.ledgers.map((value: any) => {
                return new Ledger(value.id, value.name)
            })
            this.setLedgers(ledgers, json.hasMore)
        }).catch((error) => {
            alert("We couldn't load the invoices. Please reload the page to try again")
            console.error(error)
            this.setState({
                isLoading: false
            });
        })
    }

    render() {
        return (
            <div>
                <Backdrop
                    sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                    open={this.state.isLoading}
                >
                    <CircularProgress color="inherit" />
                </Backdrop>
                <TableContainer component={Paper}>
                    <Table sx={{ minWidth: 650 }} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Amount</TableCell>
                                <TableCell align="left">Description</TableCell>
                                <TableCell align="left">Datetime</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {this.state.ledgers.map((row, index) => (
                                <TableRow
                                    key={index}
                                    sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                >
                                    <TableCell component="th" scope="row">
                                        {row.id}
                                    </TableCell>
                                    <TableCell align="left">{row.name}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                <Button variant="text" onClick={() => {
                    this.getData(true)
                }}>Previous</Button>
                <Button variant="text"  disabled={!this.state.hasMore} onClick={() => {
                    this.getData(false)
                }}>Next</Button>
            </div>
        );
    }
}
