import React, {Component} from "react";
import axios from 'axios';
import {LedgerDetail} from "../model/LedgerDetail";
import LedgerDetailTable from "./LedgerDetailTable";

export class LedgerDetailView extends Component {

    state = {
        invoices: [],
        already_called: false
    }

    getData() {
        return axios.get("http://localhost:8080/invoices").then(response => {
            const data = response.data.map((value: any) => {
                return new LedgerDetail(
                    value.amount,
                    value.currency,
                    value.is_credit,
                    value.description,
                    value.created_at
                );
            });
            this.setState({
                invoices: data
            })
        }).catch(error => {
            console.error(error)
            this.setState({
                invoices: []
            });
        });
    }

    componentDidMount() {
        if (!this.state.already_called) {
            this.getData();
            this.setState(
                {
                    already_called: true
                }
            )
        }
    }

    render() {
        return (
            <div>
                <p className="simple-header">My Ledger</p>
                <p className="simple-entries">Total Entries: {this.state.invoices.length}</p>
                <LedgerDetailTable ledgerDetails={this.state.invoices}></LedgerDetailTable>
            </div>
        );
    }

}
