import TableContainer from "@mui/material/TableContainer";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import TableCell from "@mui/material/TableCell";
import TableBody from "@mui/material/TableBody";
import * as React from "react";
import {Component} from "react";
import {Ledger} from "../model/Ledger";
import axios from "axios";
import axiosRetry from "axios-retry";
import Backdrop from "@mui/material/Backdrop";
import CircularProgress from "@mui/material/CircularProgress";
import Button from "@mui/material/Button";
import LedgerDetailTable from "../components/LedgerDetailTable";
import {Box, Modal, TextField} from "@mui/material";
import {LedgerEntry} from "../model/LedgerEntry";
import {LedgerDetail} from "../model/LedgerDetail";

export class LedgersList extends Component {

    state: {ledgers: Ledger[], isLoading: boolean, idsSeen: number[], hasMore: boolean, modalOpen: boolean, ledgerName: string, idsSeenIsEmpty: boolean} = {
        ledgers: [],
        isLoading: false,
        idsSeen: [],
        hasMore: false,
        modalOpen: false,
        ledgerName: "",
        idsSeenIsEmpty: true
        // currentLastId: null
    }

    setLedgers(ledgers: Ledger[], hasMore: boolean) {

        let idsSeenCopy = [...this.state.idsSeen];
        idsSeenCopy.push(ledgers[ledgers.length - 1].id as number);
        console.log("idsSeenCopy" + idsSeenCopy);
        this.setState({
            isLoading: false,
            ledgers: ledgers,
            hasMore: hasMore,
            idsSeen: idsSeenCopy,
            idsSeenIsEmpty: idsSeenCopy.length == 0
        });
    }

    componentDidMount() {
        this.getData(false)
    }

    async getData(isPrevious: Boolean) {
        console.log("lastIdSeen: " + this.state.idsSeen);
        this.setState({
            isLoading: true
        });
        let idsSeenCopy = [...this.state.idsSeen];
        if (this.state.idsSeen.length > 0) {
            if (isPrevious) {
                idsSeenCopy = idsSeenCopy.slice(0, -2);
                console.log("is previous newArray" + idsSeenCopy)
                this.setState({ idsSeen: idsSeenCopy });
            }
        }
        axiosRetry(axios, { retries: 3});
        let lastIdSeen
        if (idsSeenCopy.length == 0) {
            lastIdSeen = null
        }
        else {
            lastIdSeen = idsSeenCopy[idsSeenCopy.length - 1]
        }
        console.log("lastIdSeen" + lastIdSeen);
        axios.get("http://localhost:8080/ledger" , { params: { lastId : lastIdSeen, limit: 10}}).then((response) => {
            const json = response.data;
            const ledgers = json.ledgers.map((value: any) => {
                return new Ledger(value.id, value.name)
            })
            this.setLedgers(ledgers, json.hasMore)
        }).catch((error) => {
            alert("We couldn't load the invoices. Please reload the page to try again")
            console.error(error)
            this.setState({
                isLoading: false
            });
        })
    }

    handleOpen()  { this.setState({modalOpen: true}); }
    handleClose () { this.setState({modalOpen: false, ledgerName: ""}); }

    setName(name: string) { this.setState({ledgerName: name})}

    hasSeenIds() { return this.state.idsSeen.length == 0 }

    addLedger() {
        axiosRetry(axios, { retries: 3});
        axios.post("http://localhost:8080/ledger", {ledgerName: this.state.ledgerName}).then((response) => {
            const value = response.data
            const newLedger = new Ledger(value.id, value.name);
            let ledgersCopy = [...this.state.ledgers];
            ledgersCopy.unshift(newLedger)
            console.log("ledgersCopy.length: " + ledgersCopy.length);
            let hasMore = false;
            if (ledgersCopy.length > 10) {
                ledgersCopy = ledgersCopy.slice(0, -1);
                hasMore = true
            }
            this.setLedgers(ledgersCopy, hasMore)
        }).catch((error) => {
            alert("We couldn't load the invoices. Please reload the page to try again")
            console.error(error)
            this.setState({
                isLoading: false,
            });
        });
    }
    readonly style = {
        position: 'absolute' as 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 400,
        bgcolor: 'background.paper',
        border: '2px solid #000',
        boxShadow: 24,
        pt: 2,
        px: 4,
        pb: 3,
    };


    render() {
        return (
            <div>
                <Backdrop
                    sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                    open={this.state.isLoading}
                >
                    <CircularProgress color="inherit" />
                </Backdrop>
                <Button  onClick={() => {
                    this.handleOpen()
                }}>Add Ledger</Button>
                <Modal
                    open={this.state.modalOpen}

                    onClose={() => this.handleClose()}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={{...this.style, width: 200 }}>
                        <TextField id="outlined-basic" label="Ledger name" variant="outlined" onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
                            this.setName(event.target.value);
                        }}/>
                        <Button onClick={() => {
                            this.handleClose()
                        }}>Cancel</Button>
                        <Button onClick={() => {
                            this.addLedger()
                        }}>Add Ledger</Button>
                    </Box>
                </Modal>
                <TableContainer component={Paper}>
                    <Table sx={{ minWidth: 650 }} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Amount</TableCell>
                                <TableCell align="left">Description</TableCell>
                                <TableCell align="left">Datetime</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {this.state.ledgers.map((row, index) => (
                                <TableRow
                                    key={index}
                                    sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                >
                                    <TableCell component="th" scope="row">
                                        {row.id}
                                    </TableCell>
                                    <TableCell align="left">{row.name}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                <Button variant="text"  disabled={!this.state.idsSeenIsEmpty} onClick={() => {
                    this.getData(true)
                }}>Previous</Button>
                <Button variant="text"  disabled={!this.state.hasMore} onClick={() => {
                    this.getData(false)
                }}>Next</Button>
            </div>
        );
    }
}
